import User from 'App/Models/User';
import * as nodemailer from 'nodemailer';

export async function sendEmailPayment(nome: string, email: string, formaPagamento: string, valor: string) {

    try{
        
        const transporter = nodemailer.createTransport({
            host: `${process.env.EMAIL_SMTP}`,
            port: `${process.env.EMAIL_PORT}`,
            secure: true,
            auth: {
                user: `${process.env.EMAIL_SENDER}`,
                pass: `${process.env.EMAIL_PASS}`
            }
        })
            
        const mailOptions = {
            from: process.env.EMAIL_SENDER,
            to: email,
            subject: 'SOS Laudos - Compra de Créditos',
            html:
            `
                <html xmlns="http://www.w3.org/1999/xhtml" lang="pt-br" xml:lang="pt-br">
                    <head>
                        <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>
                        <style>
                            body {
                                display: flex;
                                justify-content: center;
                                align-items: center;
                                height: 40vh;
                                margin: 0;
                            }

                            .container {
                                text-align: center;
                            }

                            img {
                                width: 100px; /* Ajuste o tamanho do logo conforme necessário */
                            }
                        </style>
                    </head>
                    <body>
                        <div class="container">
                            <img src="data:image/png;base64," width="200" alt="SOS Laudos"/>
                            
                            <p><strong>Obrigado</strong> por utilizar a SOS LAUDOS, <strong>${nome}</strong></p>
                            <p>Esperamos que você tenha gostado de utilizar nossos serviços.</p>

                            <p style={"background-color: #f9f9f9"}><strong>TOTAL: R$ ${valor.replace('.00','')},00</strong></p>

                            <p>Meio de Pagamento</p>
                            <p><strong>${formaPagamento}</strong></p>
                        </div>
                    </body>
                </html>
            `
        }

        await transporter.sendMail(mailOptions, (err, info) => {
            if (err) {
                return console.log(err)
            }
            console.log(info)
        })

    }catch(error){
        return error
    }
}

function gerarSenha() {
    const caracteres = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let senha = '';
    const comprimentoSenha = 10;
  
    for (let i = 0; i < comprimentoSenha; i++) {
      const indiceAleatorio = Math.floor(Math.random() * caracteres.length);
      senha += caracteres.charAt(indiceAleatorio);
    }
  
    return senha;
  }

export async function sendResetPassword(email: string) {
    try{
        
        const novaSenha = gerarSenha()

        const transporter = nodemailer.createTransport({
            host: `${process.env.EMAIL_SMTP}`,
            port: `${process.env.EMAIL_PORT}`,
            secure: true,
            auth: {
                user: `${process.env.EMAIL_SENDER}`,
                pass: `${process.env.EMAIL_PASS}`
            }
        })
            
        const mailOptions = {
            from: process.env.EMAIL_SENDER,
            to: email,
            subject: 'SOS Laudos - Reset de Senha',
            html:
            `
                <html xmlns="http://www.w3.org/1999/xhtml" lang="pt-br" xml:lang="pt-br">
                    <head>
                        <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>
                        <style>
                            body {
                                display: flex;
                                justify-content: center;
                                align-items: center;
                                height: 40vh;
                                margin: 0;
                            }

                            .container {
                                text-align: center;
                            }

                            img {
                                width: 100px; /* Ajuste o tamanho do logo conforme necessário */
                            }
                        </style>
                    </head>
                    <body>
                        <div class="container">
                            <img src="data:image/png;base64," width="200" alt="SOS Laudos"/>
                            
                            <p><strong>Obrigado</strong> por utilizar a SOS LAUDOS, <strong>Usuário</strong></p>
                            <p>Esperamos que você tenha gostado de utilizar nossos serviços.</p>

                            <p style={"background-color: #f9f9f9"}><strong>NOVA SENHA TEMPORÁRIA: ${novaSenha}</strong></p>

                            <p>Favor alterar a senha em seu próximo login</p>
                        </div>
                    </body>
                </html>
             `
        }
        const user = await User.findBy('email', email)
        if (user){
            const nUser = user!
            nUser.password = novaSenha
            await user.merge(nUser).save()    
            await transporter.sendMail(mailOptions, (err, info) => {
                if (err) {
                    console.log(err)
                } else (
                    console.log(info)
                )
            })
            return 200
        }
        console.log(user)
        return 403
    }catch(error){-
        console.log(error)
        return 500
    }
}